// https://polynado.cash
/*
*                   888                                 888                                         888      
*                   888                                 888                                         888      
*                   888                                 888                                         888      
* 88888b.   .d88b.  888 888  888 88888b.   8888b.   .d88888  .d88b.       .d8888b  8888b.  .d8888b  88888b.  
* 888 "88b d88""88b 888 888  888 888 "88b     "88b d88" 888 d88""88b     d88P"        "88b 88K      888 "88b 
* 888  888 888  888 888 888  888 888  888 .d888888 888  888 888  888     888      .d888888 "Y8888b. 888  888 
* 888 d88P Y88..88P 888 Y88b 888 888  888 888  888 Y88b 888 Y88..88P d8b Y88b.    888  888      X88 888  888 
* 88888P"   "Y88P"  888  "Y88888 888  888 "Y888888  "Y88888  "Y88P"  Y8P  "Y8888P "Y888888  88888P' 888  888 
* 888                        888                                                                             
* 888                   Y8b d88P                                                                             
* 888                    "Y88P"                                                                              
*
* fork of https://tornado.cash
*/

pragma solidity 0.5.17;

import "./Polynado.sol";

contract ERC20Polynado is Polynado {
  address public token;

  constructor(
    IVerifier _verifier,
    uint256 _denomination,
    uint32 _merkleTreeHeight,
    address _operator,
    address _token
  ) Polynado(_verifier, _denomination, _merkleTreeHeight, _operator) public {
    token = _token;
  }

  function _processDeposit() internal {
    require(msg.value == 0, "MATIC value is supposed to be 0 for ERC20 instance");
    _safeErc20TransferFrom(msg.sender, address(this), denomination);
  }

  function _processWithdraw(address payable _recipient, address payable _relayer, uint256 _fee, uint256 _refund) internal {
    require(msg.value == _refund, "Incorrect refund amount received by the contract");

    _safeErc20Transfer(_recipient, denomination - _fee);
    if (_fee > 0) {
      _safeErc20Transfer(_relayer, _fee);
    }

    if (_refund > 0) {
      (bool success, ) = _recipient.call.value(_refund)("");
      if (!success) {
        // let's return _refund back to the relayer
        _relayer.transfer(_refund);
      }
    }
  }

  function _safeErc20TransferFrom(address _from, address _to, uint256 _amount) internal {
    (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd /* transferFrom */, _from, _to, _amount));
    require(success, "not enough allowed tokens");

    // if contract returns some data lets make sure that is `true` according to standard
    if (data.length > 0) {
      require(data.length == 32, "data length should be either 0 or 32 bytes");
      success = abi.decode(data, (bool));
      require(success, "not enough allowed tokens. Token returns false.");
    }
  }

  function _safeErc20Transfer(address _to, uint256 _amount) internal {
    (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb /* transfer */, _to, _amount));
    require(success, "not enough tokens");

    // if contract returns some data lets make sure that is `true` according to standard
    if (data.length > 0) {
      require(data.length == 32, "data length should be either 0 or 32 bytes");
      success = abi.decode(data, (bool));
      require(success, "not enough tokens. Token returns false.");
    }
  }
}
